// src/components/Login.js
import React, { useState } from 'react';
import './Login.css';
import loginPhoto from '../assets/loginPhoto.jpg';

export default function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [graduationYear, setGraduationYear] = useState('');
  const [isSignUp, setIsSignUp] = useState(false); // Toggle between Sign In and Sign Up
  const [loading, setLoading] = useState(false);

  const isCollegeEmail = email.endsWith('@college.edu');
  const isAdminEmail = email.endsWith('@admin.com');
  const isAlumniEmail = !isCollegeEmail && !isAdminEmail && email.includes('@');

  const handleSubmit = async () => {
    if (!email || !name) {
      alert('Please fill in all fields');
      return;
    }

    if (isAlumniEmail && !graduationYear) {
      alert('Please enter your graduation year');
      return;
    }

    setLoading(true);

    try {
      const endpoint = isSignUp ? '/api/users/register' : '/api/users/login';
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name,
          email,
          graduationYear: isAlumniEmail ? graduationYear : null,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Store token in localStorage for future requests
        localStorage.setItem('token', data.token);
        onLogin(data);
      } else {
        alert(data.message || 'Authentication failed');
      }
    } catch (error) {
      console.error('Authentication error:', error);
      alert('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <img src={loginPhoto} alt="Campus" className="login-image" />
      </div>
      
      <div className="login-right">
        <div className="login-form">
          <h1>A L M A</h1>
          <p>Alumni Linking and Management Application</p>
          
          {/* Toggle buttons for Sign In / Sign Up */}
          <div className="auth-toggle">
            <button 
              type="button"
              className={`toggle-btn ${!isSignUp ? 'active' : ''}`}
              onClick={() => setIsSignUp(false)}
            >
              Sign In
            </button>
            <button 
              type="button"
              className={`toggle-btn ${isSignUp ? 'active' : ''}`}
              onClick={() => setIsSignUp(true)}
            >
              Sign Up
            </button>
          </div>

          <div className="form-group">
            <input
              type="text"
              placeholder="Full Name"
              value={name}
              onChange={e => setName(e.target.value)}
              className="login-input"
            />
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={e => setEmail(e.target.value)}
              className="login-input"
            />
            
            {isAlumniEmail && (
              <input
                type="number"
                placeholder="Graduation Year (e.g., 2020)"
                value={graduationYear}
                onChange={e => setGraduationYear(e.target.value)}
                className="login-input"
              />
            )}
          </div>

          <button 
            onClick={handleSubmit} 
            className="login-button"
            disabled={loading}
          >
            {loading ? 'Processing...' : (isSignUp ? 'Sign Up' : 'Sign In')}
          </button>

          <div className="login-info">
            <p>
              Students: Use @college.edu email<br />
              Admins: Use @admin.com email<br />
              Alumni: Any other email + graduation year
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
