import React, { useState } from 'react';
import './Login.css';
import loginPhoto from '../assets/loginPhoto.jpg';

export default function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [graduationYear, setGraduationYear] = useState('');

  const isCollegeEmail = email.endsWith('@college.edu');
  const isAdminEmail = email.endsWith('@admin.com');
  const isAlumniEmail = !isCollegeEmail && !isAdminEmail && email.includes('@');

  const handleLogin = () => {
    if (!email || !name) {
      alert('Please fill in all fields');
      return;
    }
    
    if (isAlumniEmail && !graduationYear) {
      alert('Please enter your graduation year');
      return;
    }

    let role = 'alumni';
    if (isAdminEmail) role = 'admin';
    else if (isCollegeEmail) role = 'student';

    onLogin({ email, name, graduationYear, role });
  };

  return (
    <div className="login-page">
      <div className="login-container scale-in">
        <div className="login-header">
          <h1>A L M A</h1>
          <p>Alumni Linking and Management Application</p>
        </div>
        
        <div className="login-form">
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={e => setName(e.target.value)}
            className="login-input"
          />
          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={e => setEmail(e.target.value)}
            className="login-input"
          />
          
          {isAlumniEmail && (
            <input
              type="number"
              placeholder="Graduation Year (e.g., 2020)"
              value={graduationYear}
              onChange={e => setGraduationYear(e.target.value)}
              className="login-input"
            />
          )}
          
          <button onClick={handleLogin} className="btn btn-primary login-btn">
            Sign In
          </button>
          
          <div className="login-hints">
            <small>Students: Use @college.edu email</small>
            <small>Admins: Use @admin.com email</small>
            <small>Alumni: Any other email + graduation year</small>
          </div>
        </div>
      </div>
    </div>
  );
}
