import React, { useState } from 'react';
import './Login.css';
import loginPhoto from '../assets/loginPhoto.jpg';

export default function Login({ onLogin }) {
  const [showSignUp, setShowSignUp] = useState(false);
  const [loginFields, setLoginFields] = useState({ email: '', name: '' });
  const [signupFields, setSignupFields] = useState({ email: '', name: '', graduationYear: '' });

  // Detect role for signup
  const isCollegeEmail = signupFields.email.endsWith('@college.edu');
  const isAdminEmail = signupFields.email.endsWith('@admin.com');
  const isAlumniEmail = !isCollegeEmail && !isAdminEmail && signupFields.email.includes('@');

  // Handle login submit
  const handleLogin = async () => {
    if (!loginFields.email || !loginFields.name) {
      alert('Please fill in all fields');
      return;
    }
    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(loginFields)
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('token', data.token);
        onLogin(data);
      } else {
        alert(data.message || 'Sign in failed');
      }
    } catch (err) {
      alert('Network error');
    }
  };

  // Handle signup submit
  const handleSignup = async () => {
    if (!signupFields.email || !signupFields.name || (isAlumniEmail && !signupFields.graduationYear)) {
      alert('Please fill in all fields');
      return;
    }
    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(signupFields)
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('token', data.token);
        setShowSignUp(false);
        onLogin(data);
      } else {
        alert(data.message || 'Sign up failed');
      }
    } catch (err) {
      alert('Network error');
    }
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <img src={loginPhoto} alt="Campus" className="login-image" />
      </div>
      <div className="login-right">
        <div className="login-form">
          <h1>A L M A</h1>
          <p>Alumni Linking and Management Application</p>
          {!showSignUp ? (
            <>
              <input
                type="text"
                placeholder="Full Name"
                value={loginFields.name}
                onChange={e => setLoginFields({ ...loginFields, name: e.target.value })}
                className="login-input"
              />
              <input
                type="email"
                placeholder="Email Address"
                value={loginFields.email}
                onChange={e => setLoginFields({ ...loginFields, email: e.target.value })}
                className="login-input"
              />
              <button className="login-button" onClick={handleLogin}>
                Sign In
              </button>
              <button
                className="signup-button"
                onClick={() => setShowSignUp(true)}
                style={{ marginTop: 12 }}
              >
                Sign Up
              </button>
              <div className="login-info">
                <p>
                  Students: Use @college.edu email<br />
                  Admins: Use @admin.com email<br />
                  Alumni: Any other email + graduation year
                </p>
              </div>
            </>
          ) : (
            <>
              <input
                type="text"
                placeholder="Full Name"
                value={signupFields.name}
                onChange={e => setSignupFields({ ...signupFields, name: e.target.value })}
                className="login-input"
              />
              <input
                type="email"
                placeholder="Email Address"
                value={signupFields.email}
                onChange={e => setSignupFields({ ...signupFields, email: e.target.value })}
                className="login-input"
              />
              {isAlumniEmail && (
                <input
                  type="number"
                  placeholder="Graduation Year (e.g., 2020)"
                  value={signupFields.graduationYear}
                  onChange={e => setSignupFields({ ...signupFields, graduationYear: e.target.value })}
                  className="login-input"
                />
              )}
              <button className="login-button" onClick={handleSignup}>
                Sign Up
              </button>
              <button
                className="signup-button"
                onClick={() => setShowSignUp(false)}
                style={{ marginTop: 12 }}
              >
                Back to Sign In
              </button>
              <div className="login-info">
                <p>
                  Students: Use @college.edu email<br />
                  Admins: Use @admin.com email<br />
                  Alumni: Any other email + graduation year
                </p>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
